let n := 0 ; let more := true;let pos = vector 1::8 of 0;procedure add.a.queen; { n := n + 1 ; pos( n ) := 1 };procedure alterif pos( n ) = 8 thenbegin     n := n - 1     if n = 0 then more := false else alterendelse pos( n ) := pos( n ) + 1procedure cantake( int i,j -> bool )( pos( i ) = pos( j ) ) or ( abs( pos( i ) - pos( j ) ) = abs( i - j ) )procedure incheck( -> bool )begin     let check := false ; let i := 1     while i < n and ~check do     begin          if cantake( i,n ) do check := true          i := i + 1     end     checkendwhile more and n < 8 dobegin     add.a.queen     while more and incheck do alterendif more thenbegin     write "The solution to the 8 queens problem is'n"     for i = 1 to 8 do write pos( i ) : 3end else write "No solution exists'n"?